    super(props);
    // this.testsvgonclick=this.testsvgonclick.bind(this);
    this.state={
      id: props.id,
      svg: props.svg,
    };
  }
  componentWillReceiveProps(newProps){
      this.setState({
          location: newProps.location
      })
  }

  getCircle(x,y){
    return(
            <circle id={this.state.id.toHexString()} cx={x} cy={y} r='20' fill='#ffff00' strokeWidth='9' stroke='black' onClick  ={(e) => {this.props.testsvg(e);}}/>
      )
  }
  render(){
    return(
      <div>
          <h1>ETHBDISPLAY</h1>
                <div dangerouslySetInnerHTML={{__html: this.state.svg }} />
