    super(props);
    this.state={
      id: props.id,
      svg: props.svg,
    };
  }
componentWillReceiveProps(newProps){
    this.setState({
        location: newProps.location
    })
}
  render(){
    return(
      <div>
          <h1>ETHBDISPLAY</h1>
                <div dangerouslySetInnerHTML={{__html: this.state.svg }} />
                Etherbright id: {this.state.id.toHexString()}
