import React, { Component } from 'react';
import { Animate } from 'react-move';
import { SvgLoader, SvgProxy } from 'react-svgmt';

export default class EtherbrightPixelDisplay extends Component{
	constructor(props){
		super(props);
		this.state={
		pixels: props.pixels,
		pallet: props.pallet,
		id:props.id,
		c1:"#00ff00",
		c2:"#ff00ff",
		cnt:0,
		};
	}
	componentDidMount() {
    //Use React Move to animate the body
	    setInterval(() => {
	      this.setState(this.jump);
	    }, 1000);
	}
	jump(prevState){
		// console.log("JUMP ",this.state.c1);
		return{c1:this.state.pallet[this.state.cnt%4], cnt:prevState.cnt+1};

	}

	getCircle(n,x,y,c){

	    return(
	      <circle key={n} id={this.state.id.toHexString()} pn={n} cx={x} cy={y} r='20' fill={c} strokeWidth='8' stroke='black' />
	    )
	}
	 getSVG(){
  		return(
			<svg width='300' height='300'>
	          {this.state.pixels.map(pix=>(this.getCircle(pix.id, pix.xpos, pix.ypos, pix.color) ))}
	        </svg>
        )
	 }
	 animatedSVG(){
	 			// console.log("animate ",this.state.c1);
	 	// return(
	  //       <svg width="300" height="300">
	         
	  //            <circle cx="50" cy="50" r="20" fill={this.state.c1} strokeWidth='8' stroke='black' />}	          
	  //       </svg>
 		// )
	 	return(
	        <svg width="300" height="300">
	          <Animate
	            start={{ c1: "#ff00ff" }}
	            enter={{ c1: [this.state.c1] }}
	            update={{ c1: [this.state.c1] }}
	                        // duration={10}
            // easing="easePolyIn"
	          >
	            {(data) => {
	            	console.log("DATA ",{data});
	            	return(
	            	<circle cx="50" cy="50" r="20" fill={this.state.c1} strokeWidth='8' stroke='black' />
	            	)
	            	}
	        	}

	          </Animate>
	        </svg>
 		)
	 }

	render(){
		return(
			// this.getSVG()
			this.animatedSVG()
	    )
	}



}
